mapper.select:
select * from flight
and departure_airport = #{departureAirport}
and arrival_airport = #{arrivalAirport}
and departure_time >= to_date(#{departureTime} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and departure_time < to_date(#{departureTime} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
SELECT f.flight_id, f.flight_number, f.departure_time, f.arrival_time, f.flight_time, f.departure_airport, f.arrival_airport, f.user_id, f.flight_price, f.flight_status, a.airline_name, s.seats_status, COUNT(s.seats_status) AS unused_seats_count FROM flight f JOIN airline a ON f.user_id = a.user_id JOIN seats s ON f.flight_id = s.flight_id WHERE s.seats_status = '미사용' GROUP BY f.flight_id, f.flight_number, f.departure_time, f.arrival_time, f.flight_time, f.departure_airport, f.arrival_airport, f.user_id, f.flight_price, f.flight_status, a.airline_name, s.seats_status
ORDER BY
${orderItem}
ORDER BY departure_time ASC

select count(*) from flight
and departure_airport = #{departureAirport}
and arrival_airport = #{arrivalAirport}
and departure_time >= to_date(#{departureTime} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and departure_time < to_date(#{departureTime} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
SELECT f.flight_id, f.flight_number, f.departure_time, f.arrival_time, f.flight_time, f.departure_airport, f.arrival_airport, f.user_id, f.flight_price, f.flight_status, a.airline_name, s.seats_status, COUNT(s.seats_status) AS unused_seats_count FROM flight f JOIN airline a ON f.user_id = a.user_id JOIN seats s ON f.flight_id = s.flight_id WHERE s.seats_status = '미사용' GROUP BY f.flight_id, f.flight_number, f.departure_time, f.arrival_time, f.flight_time, f.departure_airport, f.arrival_airport, f.user_id, f.flight_price, f.flight_status, a.airline_name, s.seats_status
