mapper.insert:
insert into payment(
    payment_no, payment_tid, payment_name, 
    payment_total, payment_remain, user_id
) values(
    #{paymentNo}, #{paymentTid}, #{paymentName}, 
    #{paymentTotal}, #{paymentRemain}, #{userId}
)

mapper.select:
select * from payment 
where user_id = #{userId}
order by payment_no desc

select *	from payment_detail 
where payment_detail_origin = #{paymentNo} 
order by payment_detail_no asc

select * from payment 
where user_id = #{userId} 
order by payment_no desc
